mapper.select:

	select bno, title, content, writer, regdate, updatedate from 
	(
		select /*+INDEX_DESC(tbl_board pk_obard) */
			rownum rn, bno, title, content, writer, regdate, updatedate 
		from
			tbl_board
		where rownum <= #{pageNum} * ${amount}
	)
	where rn > (#{pageNum} -1) * #{amount}
